import React, { useState } from "react";
import { SketchPicker } from "react-color";
import CopyToClipboard from "react-copy-to-clipboard";

let svgUrl  = () => { '<svg width="100%" height="100%" id="svg" viewBox="0 0 1440 590" xmlns="http://www.w3.org/2000/svg" class="transition duration-300 ease-in-out delay-150"><defs><linearGradient id="gradient" x1="0%" y1="50%" x2="100%" y2="50%"><stop offset="5%" stop-color="#F78DA7"></stop><stop offset="95%" stop-color="#8ED1FC"></stop></linearGradient></defs><path d="M 0,600 C 0,600 0,200 0,200 C 90.16267942583733,186.01913875598086 180.32535885167465,172.03827751196172 268,192 C 355.67464114832535,211.96172248803828 440.8612440191388,265.86602870813397 554,272 C 667.1387559808612,278.13397129186603 808.2296650717705,236.4976076555024 896,199 C 983.7703349282295,161.5023923444976 1018.2200956937797,128.14354066985646 1100,129 C 1181.7799043062203,129.85645933014354 1310.8899521531102,164.92822966507177 1440,200 C 1440,200 1440,600 1440,600 Z" stroke="none" stroke-width="0" fill="url(#gradient)" fill-opacity="0.53" class="transition-all duration-300 ease-in-out delay-150 path-0"></path><defs><linearGradient id="gradient" x1="0%" y1="50%" x2="100%" y2="50%"><stop offset="5%" stop-color="#F78DA7"></stop><stop offset="95%" stop-color="#8ED1FC"></stop></linearGradient></defs><path d="M 0,600 C 0,600 0,400 0,400 C 110.88038277511961,402.9952153110048 221.76076555023923,405.99043062200957 320,386 C 418.2392344497608,366.00956937799043 503.8373205741626,323.03349282296654 580,343 C 656.1626794258374,362.96650717703346 722.8899521531101,445.8755980861244 811,458 C 899.1100478468899,470.1244019138756 1008.602870813397,411.46411483253587 1117,390 C 1225.397129186603,368.53588516746413 1332.6985645933014,384.26794258373207 1440,400 C 1440,400 1440,600 1440,600 Z" stroke="none" stroke-width="0" fill="url(#gradient)" fill-opacity="1" class="transition-all duration-300 ease-in-out delay-150 path-1"></path></svg>'};
const BannerForm = () => {
  const [formData, setFormData] = useState({
    imageUrl: {svgUrl},
    fontColor: "#FFFFFF",
    fontSize: "5em",
    fontSize2: "3em",
    text: "Create. Curate. Connect.",
    text2: "This is a sub-headline",
  });

  const [previewCode, setPreviewCode] = useState("");

  const generatePreviewCode = () => {
    const { imageUrl, fontColor, fontSize, fontSize2, text, text2 } = formData;
    const code =       `<div style="background-image: url(${imageUrl}); background-size: cover; background-position: center center; height: 300px; display: flex; flex-direction: column; justify-content: center; align-items: center;">
    <h1 style="color: ${fontColor}; font-size: ${fontSize}px;">${text}</h1>
    <p style="color: ${fontColor}; font-size: ${fontSize2}px;">${text2}</p>
    </div>`
    setPreviewCode(code);
  };  

  const [codeSnippet, setCodeSnippet] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleColorChange = (color) => {
    setFormData({ ...formData, fontColor: color.hex });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    generatePreviewCode();
    const { imageUrl, fontColor, fontSize, fontSize2, text, text2 } = formData;
    setCodeSnippet(
      `<div style="background-image: url(${imageUrl}); background-size: cover; background-position: center center; height: 300px; display: flex; flex-direction:column; justify-content: center; align-items: center;">
      <h1 style="color: ${fontColor}; font-size: ${fontSize}px;">${text}</h1>
      <p style="color: ${fontColor}; font-size: ${fontSize2}px;">${text2}</p>
      </div>`
    );
  };


  return (
    <div class="container my-4">
      <form onSubmit={handleSubmit} className="row">
  <div className="col-md-6">
  <div className="form-group mb-4">
    <label htmlFor="imageUrl">Background Image URL:</label>
    <input
      type="text"
      name="imageUrl"
      defaultValue="My default value"
      value={formData.imageUrl}
      onChange={handleChange}
      className="form-control"
      id="imageUrl"
    />
  </div>
  <div className="form-group mb-4">
    <label htmlFor="fontColor">Font Color:</label>
    <SketchPicker
      color={formData.fontColor}
      onChangeComplete={handleColorChange}
    />
  </div>
  </div>
  <div className="col-md-6">
  <div className="form-group mb-4">
    <label htmlFor="fontSize">Headline Font Size:</label>
    <input
      type="text"
      name="fontSize"
      value={formData.fontSize}
      onChange={handleChange}
      className="form-control"
      id="fontSize"
    />
  </div>
  <div className="form-group mb-4">
    <label htmlFor="fontSize">Sub-Headline Font Size:</label>
    <input
      type="text"
      name="fontSize2"
      value={formData.fontSize2}
      onChange={handleChange}
      className="form-control"
      id="fontSize2"
    />
  </div>
  <div className="form-group mb-4">
    <label htmlFor="text">Headline:</label>
    <input
      type="text"
      name="text"
      value={formData.text}
      onChange={handleChange}
      className="form-control"
      id="text"
    />
  </div>
  <div className="form-group mb-4">
    <label htmlFor="text">Sub-headline:</label>
    <input
      type="text"
      name="text2"
      value={formData.text2}
      onChange={handleChange}
      className="form-control"
      id="text2"
    />
  </div>
  <button type="submit" className="btn btn-primary mb-4">
    Generate Code
  </button>
  </div>
</form>

      <br />
      {codeSnippet && (
        <div className="row">
  <div className="col-md-6">
    <h4>Preview:</h4>
    <div dangerouslySetInnerHTML={{ __html: previewCode }}></div>
  </div>
  <div className="col-md-6">
    <h4>Code Snippet:</h4>
    <div className="form-group">
      <textarea
        className="form-control"
        id="codeSnippet"
        value={codeSnippet}
        readOnly
      />
    </div>
    <CopyToClipboard text={codeSnippet}>
      <button type="button" className="btn btn-primary my-4">
        Copy Code
      </button>
    </CopyToClipboard>
  </div>
</div>

)}

    </div>
  );
};

export default BannerForm;
